package amazon;

import com.exercises.amazon.hackerrank.main.Hasher;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

public class Task1Test {

    @Test
    void testAmazonWord() {
        assertEquals(1, Hasher.countNumWays("amazon", 3));
    }

    @Test
    void testabaaWord() {
        assertEquals(1, Hasher.countNumWays("abaa", 4));
    }

    @Test
    void veryLongString() {
        long startTime = System.nanoTime();
        assertEquals(19, Hasher.countNumWays("", 20));
        long endTime = System.nanoTime();
        long duration = (endTime - startTime);
        System.out.println("Time elapsed in milliseconds is: " + duration/1000000);
    }

    @Test
    void isSingleChar() {
        assertTrue(Hasher.containsOnlyOneChar("yyyyyyyyyyy"));
    }

}
