package amazon;

import com.exercises.amazon.hackerrank.main.Hasher;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class Task1Test {

    @Test
    void testAmazonWord() {
        assertEquals(1, Hasher.countNumWays("amazon", 3));
    }

    @Test
    void testabaaWord() {
        assertEquals(1, Hasher.countNumWays("abaa", 4));
    }

    @Test
    void veryLongString() {
        assertEquals(19, Hasher.countNumWays("", 20));
    }

}
